import React from "react"
import '../App.css'
import {beginWithWord} from '../helpers/helpers'

class CategoryPopup extends React.Component {
  constructor(props){
    super(props);
    this.setIndex = this.setIndex.bind(this);
    this.sports = ['eagles', 'flames', 'pelicans', 'heat', 'thunder', 'penguins', 'raptors', 'leafs', 'oilers', 'cowboys'];
    this.prog = ['python', 'ruby', 'java', 'scratch', 'javascript', 'typescript', 'bash', 'sql', 'swift', 'html'];
    this.selectedWord = " "
}

  setIndex(i) {
    this.setState({ selectedWord: beginWithWord(i, this.state.sports, this.state.prog)});
    console.log(this.state.selectedWord)
  }
  render() {
    let selectedWord = this.state.selectedWord;
    const initialMessage = "Choose a Category";
  return(
    <div className="category-popup-container" style={selectedWord === ' ' ? {display: 'flex'} : {}}>
     <div className="c-popup">
       <h2>{initialMessage}</h2>
       <button onClick={() => this.setIndex(0)}>Sports Teams</button>
       <button onClick={() => this.setIndex(1)}>Programming Languages</button>
     </div>
   </div>
  )
}
}

export default CategoryPopup
