import React from "react"
import {beginWithWord} from "../helpers/helpers"

const CategoryPopup = ({selectedWord, sports, prog}) => {
  let initialMessage = "Choose a Category";

  function setIndex(index) {
    let i = index;
    selectedWord = beginWithWord(i, sports, prog);
    initialMessage = '';
  }

  return(
    <div className="category-popup-container" style={initialMessage !== '' ? {display: 'flex'} : {}}>
     <div className="popup">
       <h2>{initialMessage}</h2>
       <button onClick={setIndex(0)}>Sports Teams</button>
       <button onClick={setIndex(1)}>Programming Languages</button>
     </div>
   </div>
  )
}

export default CategoryPopup
