import React from "react"
import '../App.css'
import {beginWithWord} from '../helpers/helpers'

const sports = ['eagles', 'flames', 'pelicans', 'heat', 'thunder', 'penguins', 'raptors', 'leafs', 'oilers', 'cowboys'];
const prog = ['python', 'ruby', 'java', 'scratch', 'javascript', 'typescript', 'bash', 'sql', 'swift', 'html'];
const initialMessage = "Choose a Category";

const CategoryPopup = ({selectedWord}) => {

  function setIndex(i) {
    selectedWord = beginWithWord(i, sports, prog);
    console.log(selectedWord);
  }


  return(
    <div className="category-popup" style={selectedWord === ' ' ? {display: 'flex'} : {}}>
     <div className="c-popup">
       <h2>{initialMessage}</h2>
       <button onClick={() => setIndex(0)}>Sports Teams</button>
       <button onClick={() => setIndex(1)}>Programming Languages</button>
     </div>
   </div>
  )
}


export default CategoryPopup
