import React from "react"
import '../App.css'
import {beginWithWord} from '../helpers/helpers'
import WrongLetters from "./WrongLetters"
import Word from "./Word"

const sports = ['eagles', 'flames', 'pelicans', 'heat', 'thunder', 'penguins', 'raptors', 'leafs', 'oilers', 'cowboys'];
const prog = ['python', 'ruby', 'java', 'scratch', 'javascript', 'typescript', 'bash', 'sql', 'swift', 'html'];

class CategoryPopup extends React.Component {
  constructor(props){
    super(props);
    this.setIndex = this.setIndex.bind(this);
    this.state = {selectedWord: " " };
}

  setIndex(i) {
    this.setState({ selectedWord: beginWithWord(i, sports, prog)});
     let correctLetters= [];
       console.log(this.state.selectedWord)
    return(
      <Word selectedWord={this.state.selectedWord} correctLetters={correctLetters}/>
    );
  }
  render() {
    let selectedWord = this.state.selectedWord;
    const initialMessage = "Choose a Category";
  return(
    <div className="category-popu" style={selectedWord === ' ' ? {display: 'flex'} : {}}>
     <div className="c-popup">
       <h2>{initialMessage}</h2>
       <button onClick={() => this.setIndex(0)}>Sports Teams</button>
       <button onClick={() => this.setIndex(1)}>Programming Languages</button>
     </div>
   </div>
  )
}
}

export default CategoryPopup
