import React from "react"
import './App.css';
import Header from "./components/Header"
import Figure from "./components/Figure"
import WrongLetters from "./components/WrongLetters"
import Word from "./components/Word"
import Popup from "./components/Popup"
import CategoryPopup from "./components/CategoryPopup"
import Notification from "./components/Notification"


class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      playable: false,
      correctLetters: [],
      wrongLetters: [],
      showNotification: false,
    };
    this.playAgain=this.playAgain.bind(this);
  }


  playAgain() {
    // Empty Arrays

    this.setState({
      playable:false,
      correctLetters: [],
      wrongLetters: [],
    });
  }

render() {
  return (
    <>
    <Header />
    <div className="game-container">
      <CategoryPopup selectedWord={this.state.selectedWord}/>
      <Figure wrongLetters={this.state.wrongLetters} />
      <WrongLetters wrongLetters={this.state.wrongLetters} />
      <Word selectedWord={this.state.selectedWord} correctLetters={this.state.correctLetters}/>*
    </div>
    <Popup correctLetters={this.state.correctLetters} wrongLetters={this.state.wrongLetters} selectedWord={this.state.selectedWord} playable={this.state.playable} playAgain={this.playAgain}/>
    <Notification showNotification={this.state.showNotification} />
    </>
  );
}
}
export default App;
